/** Название data-аттрибута для метки меню в jsx */
declare const MENU_STAMP_ATTR = "menu-stamp";
/**
 * Определение кликов вне контекстного меню
 * @description При текущей реализации контекстные меню второго уровня
 * не являются дочерними элементами меню первого уровня.
 * Из-за этого нажатие по ним считаются за нажатия вне контекстного меню.
 *
 * Данный метод использует то, что подменю устанавливается такой же data-аттрибут, что и у внешнего меню.
 */
declare function useClickOutsideMenu(menuStamp: string, callback?: VoidFunction): void;
export { MENU_STAMP_ATTR, useClickOutsideMenu };
