/** Тип медиа для предпросмотра */
declare enum MediaType {
    Image = 0,
    Video = 1,
    Audio = 2,
    Other = 3
}
/** Типы для которых доступен предпросмотр */
declare const PREVIEW_MIME: Set<string>;
/** Тип объекта, который описывает поддерживаемые форматы файлов */
type FormatSet = {
    [type: string]: string;
};
/** Форматы изображений, которые должны поддерживаться */
declare const IMAGE_FORMAT: FormatSet;
/** Форматы видео, которые должны поддерживаться */
declare const VIDEO_FORMAT: FormatSet;
/** Форматы аудио, которые должны поддерживаться */
declare const AUDIO_FORMAT: FormatSet;
/** Получение типа медиа из типа файла */
declare function getMediaType(file: {
    type: string;
    name: string;
}): MediaType;
export { MediaType, PREVIEW_MIME, FormatSet, IMAGE_FORMAT, VIDEO_FORMAT, AUDIO_FORMAT, getMediaType };
