/**
 * Проверка параданного формата на принадлежность к типу DateFormat
 * @param {string} format строка формата
 * @returns {boolean} результат проверки
 */
declare const isDateFormat: (format: string) => format is "dayMonth" | "monthDay" | "dayMonthYear" | "monthDayYear";
/**
 * Проверка параданного формата на принадлежность к типу TimeFormat
 * @param {string} format строка формата
 * @returns {boolean} результат проверки
 */
declare const isTimeFormat: (format: string) => format is "hoursMinutes" | "minutesSeconds" | "hoursMinutesSeconds";
export { isDateFormat, isTimeFormat };
