import { TimeFormat } from "../types";
type TimePeriod = 'hours' | 'minutes' | 'seconds';
type TimePeriodIndexes = {
    [key in TimePeriod]?: number;
};
/** Парсит переданный формат времени, определяя в нём индексы часов, минут и секунд (если есть)
 * @param {DateFormat} format формат для парсинга
 * @returns {TimePeriodIndexes} объект, в котором указаны индексы позиций,
 * на которых ожидаются для ввода значения часов, минут и секунд (если они есть в переданном формате)
 */
declare const parseTimeFormat: (format: TimeFormat) => TimePeriodIndexes;
/**
 * Функция проверки на НЕвалидность времени в соответствии с переданным форматом
 * @param {string} value время
 * @param {DateFormat} format формат, которому должно соответствовать время
 * @returns {boolean} возвращает "true" если введенное значение не прошло валидацию
 */
declare const checkTimeInvalid: (value: string, format: TimeFormat) => boolean;
export { parseTimeFormat, checkTimeInvalid };
