/// <reference types="react" />
import React from 'react';
type SizeMark = 'default' | 'small';
interface MarkProps {
    /** Текста  метки в формате строки */
    text: string;
    /** Размер текста */
    size?: SizeMark;
    /** Функция клика по крестику */
    onClose?: React.MouseEventHandler<HTMLButtonElement>;
    /** Иконка слева от контента метки */
    iconLeft?: React.SVGProps<SVGSVGElement>;
    /** Иконка справа от контента метки */
    iconRight?: React.SVGProps<SVGSVGElement>;
    /** ID для тестирования */
    dataTestId?: string;
    /** ID для удаления(делегирование событий)
     * Можно найти в event.target.dataset.id или event.target.getAttribute("data-id") */
    dataId?: string;
    /** Цвет текста */
    textColor?: string;
    /** Цвет фона */
    backgroundColor?: string;
    /** Флаг дизейбла метки */
    disabled?: boolean;
}
export { MarkProps, SizeMark };
