/// <reference types="react" />
import React from 'react';
/**
 * Хук для отмены стандартного действия браузера и
 * вызова переданного колбэка при наступлении события 'contextmenu' на целевом элементе.
 *
 * Может быть кейс, когда ссылка внутри ref изменится,
 * но обработчик будет висеть на старом элементе из-за специфики работы ref.
 * Чтобы правильно обновлять ссылку на целевой элемент необходимо передать третьим аргументом значение,
 * которое изменяется с течением времени также, как ref.
 * @param {React.RefObject<HTMLElement>} ref Ссылка на элемент, на котором будет вызываться переданный колбэк
 * @param {(event: MouseEvent) => void} callback колбэк вызываемый при попытке пользователя вызвать контекстное меню
 * @param {unknown} updateTrigger любое значение, которое меняется вместе с ref(пример: key).
 * @returns {void}
 */
declare const useContextMenu: (ref: React.RefObject<HTMLElement>, callback: (event: MouseEvent) => void, updateTrigger?: unknown) => void;
export { useContextMenu as default };
