import { RefObject } from 'react';
/**
 * Определение клика вне Select-a
 * При текущей реализации dropdown-а он может находится внутри BaseSelect, а может находится
 * в Body. Чтобы правильно скрывать dropdown используется его реф (ищем таргет с помощью contains).
 * Также нужен реф инпута, елси dropdown отрендерился внутри Select-a (ищем тагрет с помощью contains)
 */
declare const useClickOutside: (dropDownRef: RefObject<HTMLElement>, inputRef: RefObject<HTMLElement>, callback: VoidFunction, isOpenDropdown?: boolean | undefined) => void;
export { useClickOutside as default };
