import { Locale } from 'date-fns';
import { DayValue, LocaleOptions, SelectedDate } from "./types";
/** Сегодняшний день с нулевым временем */
declare function getToday(): Date;
/** Проверка, что две даты равны. Время не учитывается */
declare function dayEqual(day1: Date, day2: Date): boolean;
/** Преобразовать даты в хеш-строку */
declare function dateToHash(date: Date): string;
/** Получить количество дней в месяце */
declare function getDaysInMonth(year: number, month: number): number;
