interface ControlsType {
    /** Шрифт (жирный, курсив и т.д.) */
    formatFont?: boolean;
    /** Абзац (нумерованный/маркированный список) */
    formatList?: boolean;
    /** Возможность добавлять ссылку */
    formatLink?: boolean;
    /** Очистить форматирование */
    clearAllButton?: boolean;
}
interface ControlButtonProps {
    /** Признак видимости контрола */
    hidden?: boolean;
}
interface CopyCutPasteButtonsProps extends ControlButtonProps {
    /** Список доступных контролов для форматирования */
    controls?: ControlsType;
}
interface LinkControlProps extends ControlButtonProps {
    /** Колбэк по клику на ссылку */
    onClick(): void;
}
export { ControlsType, ControlButtonProps, CopyCutPasteButtonsProps, LinkControlProps };
