type TextFontWeight = 'bold' | 'regular' | 'inherit';
type TextSize = 'extra_large' | 'large' | 'default' | 'small' | 'extra_small' | 'inherit';
type TitleFontWeight = 'regular' | 'bold';
type TitleVariant = 'h1_default' | 'h1_large' | 'h1_extra_large' | 'h1_display' | 'h2_default' | 'h2_large' | 'h3_default' | 'h4_default';
type CaptionFontWeight = 'bold' | 'regular';
type CaptionVariant = 'caption';
type VariantModes = Exclude<TitleVariant | CaptionVariant | TextSize | 'text' | 'title', 'inherit'>;
type WeightByVariant<M> = M extends TitleVariant | 'title' ? (TitleFontWeight | false) : (M extends CaptionVariant ? (CaptionFontWeight | false) : TextFontWeight);
export { TextFontWeight, TextSize, TitleFontWeight, TitleVariant, CaptionFontWeight, CaptionVariant, VariantModes, WeightByVariant };
