declare enum PlacementEnum {
    Top = "top",
    Bottom = "bottom",
    Left = "left",
    Right = "right"
}
type TooltipPlacement = `${PlacementEnum}`;
type TooltipTailAlign = 'start' | 'center' | 'end';
interface TooltipProps {
    /** Текста тултипа */
    text: string;
    /** Расположение относительно родителя (над/под/слева/справа) */
    placement: TooltipPlacement;
    /** Размер тултипа */
    size?: 'default' | 'small';
    /** Дополнительный текст подсказки в тултипе (отличается цветом, располагается после основного текста) */
    hint?: string;
    /** Фиксированная ширина подсказки */
    width?: number;
    /** Data атрибут */
    dataTestId?: string;
    /** Z-index для компонента */
    zIndex?: number;
    /** Выравнивание треугольной стрелки относительно тултипа (начало/центр/конец). Только для placement: top/bottom */
    tailAlign?: TooltipTailAlign;
}
interface PortalTooltipProps extends TooltipProps {
    /** Элемент к которому прикрепляется tooltip */
    target: HTMLDivElement | null;
}
export { PlacementEnum, TooltipPlacement, TooltipTailAlign, TooltipProps, PortalTooltipProps };
